    handleHistoryData(message) {
      var midnight = new Date();
      midnight.setHours(0, 0, 0, 0);
      midnight.getTime() / 1000;
      console.log(message);

      let states = message.states;

      var periods = [];

      for (const [name, value] of Object.entries(states)) {
        if (name === "sensor.smart_meter_electricity_import_today") {

          let temp_periods = [];
          let prev_value = 0;
          for (var i = 0; i < value.length; i++) {
            if (value[i].s === "unavailable" || value[i].s === "unknown") {
              continue;
            }

            if (value[i].s > prev_value) {
              temp_periods.push({
                time: Math.floor(value[i].lu),
                value: value[i].s,
                mix: []
              });
            } else {
              temp_periods = [];
              temp_periods.push({
                time: Math.floor(value[i].lu),
                value: value[i].s,
                mix: []
              });
            }

            prev_value = value[i].s;
          }

          periods = temp_periods;
        }
      }


      for (var i = 0; i < periods.length; i++) {
        let currentPeriod = periods[i];
        let currentTime = currentPeriod.time;

        for (const [name, value] of Object.entries(states)) {
          if (name === "sensor.smart_meter_electricity_import_today" || name.slice(-6) === "carbon") {
            continue;
          }

          var prevValue = 0;
          for (var j = 0; j < value.length; j++) {
            if (j + 1 === value.length) {
              currentPeriod.mix.push(value[j].s);
              break
            }

            if (value[j].lu > currentTime) {
              currentPeriod.mix.push(prevValue);
              break
            }
            prevValue = value[j].s;
          }
        }
      }

      var intensities = [
        states["sensor.biomass_carbon"][0].s,
        states["sensor.coal_carbon"][0].s,
        this.average([
          Number(states["sensor.dutch_imports_carbon"][0].s),
          Number(states["sensor.french_imports_carbon"][0].s),
          Number(states["sensor.irish_imports_carbon"][0].s)
        ]),
        Number(states["sensor.gas_open_cycle_carbon"][0].s) * 0.2 +
        Number(states["sensor.gas_combined_cycle_carbon"][0].s) * 0.8,
        states["sensor.nuclear_carbon"][0].s,
        states["sensor.other_carbon"][0].s,
        states["sensor.hydro_carbon"][0].s,
        states["sensor.solar_carbon"][0].s,
        states["sensor.wind_carbon"][0].s
      ];

      var prevValue = 0;
      for (var i = 0; i < periods.length; i++) {
        let difference = Number(periods[i].value) - Number(prevValue);
        console.log('value: ' + periods[i].value);
        let total = 0.0;
        console.log('difference: ' + difference);
        console.log(intensities);

        for (var j = 0; j < intensities.length; j++) {
          total += Number(intensities[j]) * ((Number(periods[i].mix[j]) / 100) * Number(difference));
        }

        this.total_carbon += total;
        console.log("carbon: " + total);
        prevValue = Number(periods[i].value);
      }

      this.render();

      console.log(periods);
    }

